import React from 'react';
import { connect } from 'react-redux';

import { clearItemFromCart, addItem, removeItem } from '../../redux/cart/cart.actions';

// import './checkout-item.styles.scss';
import { CheckoutItemContainer, 
    ImageContainer, 
    TextContainer, 
    QuantityContainer,
    DecreaseQuantityButton } from './checkout-item.styles';

const CheckoutItem = ({ cartItem, clearItem, addItem, removeItem }) => {
    const { name, imageUrl, price, quantity } = cartItem;

    let decreaseButtonProps = { invisible: false };
    decreaseButtonProps["invisible"] = quantity < 1 ? true : false;

    return (
        <CheckoutItemContainer>
            <ImageContainer>
                <img src={imageUrl} alt='item'/>
            </ImageContainer>
            <TextContainer>{name}</TextContainer>
            <QuantityContainer>
                <span {...decreaseButtonProps} onClick={() => removeItem(cartItem)}>&#10094;</span>
                <span>{quantity}</span>
                <div onClick={() => addItem(cartItem)}>&#10095;</div>
            </QuantityContainer>
            <TextContainer>{price}</TextContainer>
            <span className='remove-button' onClick={() => clearItem(cartItem)}>&#10005;</span>
        </CheckoutItemContainer>
)};

const mapDispatchToProps = dispatch => ({
    clearItem: item => dispatch(clearItemFromCart(item)),
    addItem: item => dispatch(addItem(item)),
    removeItem: item => dispatch(removeItem(item))
})

export default connect(null, mapDispatchToProps)(CheckoutItem);
